// To parse this JSON data, do////     final task = taskFromMap(jsonString);import 'dart:convert';import 'package:json_annotation/json_annotation.dart';part 'task.g.dart';@JsonSerializable()class Task {  Task({    this.id,    this.name,    this.createdAt,    this.avatar,    this.county,  });  DateTime? createdAt;  String? name;  String? avatar;  String? id;  String? county;  factory Task.fromJson(Map<String, dynamic> json) => _$TaskFromJson(json);  Map<String, dynamic> toJson() => _$TaskToJson(this);}