import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_bloc_app/presenter/post/bloc/post_bloc.dart';import 'package:flutter_bloc_app/presenter/task/view/task_view_cubit.dart';import 'package:flutter_bloc_app/presenter/widgets/task_list.dart';class TaskViewBloc extends StatelessWidget {  const TaskViewBloc({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('Task list | Bloc'),      ),      body: BlocBuilder<PostBloc, PostState>(        builder: (context, state) => state.when(          loading: () => const Center(child: CircularProgressIndicator()),          loaded: (data) => TaskListView(list: data),          error: (error) => Center(child: Text(error.toString())),        ),      ),      floatingActionButton: FloatingActionButton(        onPressed: () {          Navigator.push(            context,            MaterialPageRoute(builder: (context) => const TaskViewCubit()),          );        },        child: const Icon(Icons.login),      ),    );  }}