import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_bloc_app/presenter/task/cubit/task_cubit.dart';import 'package:flutter_bloc_app/presenter/widgets/task_list.dart';import '../../../data/rest_client.dart';class TaskViewCubit extends StatelessWidget {  const TaskViewCubit({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => TaskCubit(RepositoryProvider.of<RestClient>(context)),      child: Scaffold(        appBar: AppBar(          title: const Text('Task list | Cubit'),        ),        body: BlocBuilder<TaskCubit, TaskState>(          builder: (context, cubit) => cubit.when(            loading: () => const Center(child: CupertinoActivityIndicator()),            loaded: (data) => TaskListView(list: data),            error: (e)=> Center(child: Text(e.toString())),          ),        ),      ),    );  }}