import 'package:flutter/material.dart';import 'package:flutter_bloc_app/domain/entities/task.dart';class TaskListView extends StatelessWidget {  final List<Task> list;  const TaskListView({Key? key, required this.list}) : super(key: key);  @override  Widget build(BuildContext context) {    return ListView.separated(      itemBuilder: (BuildContext context, int index) {        return ListTile(          title: Text(list[index].name ?? ''),          leading: CircleAvatar(child: Image.network(list[index].avatar!)),          subtitle: Text('from ${list[index].county}'),        );      },      separatorBuilder: (BuildContext context, int index) => const Divider(),      itemCount: list.length,    );  }}